import React, { Component } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { AdminConsumer } from '../contexts/adminContext'

export class loginForm extends Component {
    state = {

        username: "gurk",
        password: "123",
        admins: [{
            id: "-",
            name: "-",
            lastname: "-",
            username: "-",
            mail: "-",
            password: "-"
        }]
    }



    getAdminByUsername = (name, callback) => {
        fetch(`/api/admin/getByUsername/${name}`)
            .then(res => res.json())
            .then(admin => {
                this.setState({
                    admins: admin
                })
                callback()
            })
    }




   

    changeInput = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })

    }

    render() {
        const { username, password } = this.state
        return (
            <AdminConsumer>
                {
                    ({dispatch, isValid}) => {
                        return (
                            <div className="center" >
                            <hr />
                            <input id="username"
                                className="formCenter"
                                type="text"
                                placeholder="Username"
                                value={username}
                                onChange={this.changeInput} />
                            <br />
                            <input id="password"
                                className="formCenter"
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={this.changeInput} />
                            <hr />
                            <button id="loginButton" className="formCenter" 
                            onClick={e => dispatch({ type: "adminIsValid", payload: this.state }, () => {
                                 this.props.history.push("/index");
                            })}> 
                            Login </button>
                            <br />
                            <Link className="formCenter" to="/signup" > Sign Up </Link>
                            <br />
                            <Link className="formCenter" to="/forgatpass" > Forgat Password </Link>
                        </div>
                        );
                    }
                }
            </AdminConsumer>

        );
    }
}

export default loginForm
