import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { AdminConsumer } from '../contexts/adminContext'

export class loginForm extends Component {
    state = {

        username: "gurk",
        password: "123",
        message: "",
        admins: [{
            id: "-",
            name: "-",
            lastname: "-",
            username: "-",
            mail: "-",
            password: "-"
        }]
    };


    changeInput = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })

    };  

    render() {
        const { username, password } = this.state
        return (
            <AdminConsumer>
                {
                    ({dispatch, message}) => {
                        return (
                            <div className="center" >
                            <hr />
                            <input id="username"
                                className="formCenter"
                                type="text"
                                placeholder="Username"
                                value={username}
                                onChange={this.changeInput} />
                            <br />
                            <input id="password"
                                className="formCenter"
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={this.changeInput} />
                            <hr />
                            <button id="loginButton" className="formCenter" 
                            onClick={e => dispatch({ type: "adminIsValid", payload: this.state })}> 
                            Login </button>
                            <h6 className="formCenter" id="message" onChange={this.changeInput}   >{message}</h6>

                            <br />
                            <Link className="formCenter" to="/signup" > Sign Up </Link>
                            <br />
                            <Link className="formCenter" to="/forgatpass" > Forgat Password </Link>
                        </div>
                        );
                    }
                }
            </AdminConsumer>

        );
    }
}

export default loginForm
