import React from "react";

const Context = React.createContext();


export class AdminProvider extends React.Component {

    state = {
        isValid: false,
        loggedAdmin: [],
        message: "",
        dispatch: (action, callback) => {
            this.setState(this.reducer(action));
            callback();
        }
    };

    reducer = (action) => {
        switch (action.type) {
            case "adminIsValid":
                this.changeValid(action.payload);
                this.setState({
                    message: ""
                })
                break;
            default:
                this.setState({
                    message: "wrong admin acation"
                })
        }
    };

    changeValid = (input, callback) => {
        fetch(`/api/admin/getByUsername/${input.username}`)
            .then(res => res.json())
            .then(admins => {
                if (admins === []) return this.setState({ message: "wrong username"}) 
                for (let admin of admins) {
                    let inputPassword = input.password;
                    let password = admin.password;

                    if (inputPassword === password) {
                        this.setState({
                            message: ""
                        })
                        localStorage.setItem('current', JSON.stringify({ loggedAdmin: admin, isValid: true }));
                        return;
                    } else {
                        this.setState({ message: "wrong password"})
                    }
                }
            })
    };

    render() {
        const { state, props: { children } } = this;
        return <Context.Provider value={state}>{children}</Context.Provider>;
    }
}

export const AdminConsumer = Context.Consumer;
