import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import employeeImg from '../images/employee.svg'
import statuImg from '../images/statu.svg'
import criteriaImg from '../images/criteria.png'


export class MainNav extends Component {

    state = {
        employee: "",
        statu: "",
        criterion: "",

        employees:  JSON.parse(localStorage.getItem('employee')),
        status:  JSON.parse(localStorage.getItem('statu')),
        criteria:  JSON.parse(localStorage.getItem('criterion')),

        employeeResponse: "", 
        statuResponse:"",
        criterionResponse: "",

    }


    /* listItems = () => {
        for (let i = 0; i <this.state.e)


        
    } */



    changeInput = (e) => {
        console.log(this.state.status)
        let list;
        switch(e.target.id){
            case "employee" :
                list = this.state.employees;
                break;
                case "statu":
                    list = this.state.status;
                    break;
                    case "criterion":
                    list = this.state.criteria;
                    break;
        }
            
        this.setState({
            [e.target.id]: e.target.value,
            [`${e.target.id}Response`]: this.state.status.map((statu) =>
            <li>{statu.name}</li>)
        })
        console.log(this.state.listItems)

    };

    render() {
        const { employee, statu, criterion } = this.state
        return (
            <div className="center60">

                <div className="col3">
                    <Link to="/employee">
                        <img src={employeeImg} alt="employee" />
                        <h4><i>EMPLOYEES</i></h4>
                    </Link>
                    <input id="employee" type="text" className="search formCenter" placeholder="Search.." value={employee} onChange={this.changeInput} />
                    <ul>{this.state.employeeResponse}</ul>
                </div>
                <div className="col3">
                    <Link to="/statu">
                        <img src={statuImg} alt="statu" />
                        <h4><i>STATU</i></h4>
                    </Link>
                    <input id="statu" type="text" className="search formCenter" placeholder="Search.." value={statu} onChange={this.changeInput} />
                    <ul>{this.state.statuResponse}</ul>
                </div>
                <div className="col3">
                    <Link to="/criteria">
                        <img src={criteriaImg} alt="criteria" />
                        <h4><i>CRITERIA</i></h4>
                    </Link>
                    <input id="criterion" type="text" className="search formCenter" placeholder="Search.." value={criterion} onChange={this.changeInput} />
                    <ul>{this.state.statuResponse}</ul>
                </div>

            </div>
        )
    }
}

export default MainNav
