    }
    
     getCriteria = () => {
    
    }


    render() {
        return (
            <div>
                {
                    (this.state.currentAdmin === null) ? window.location.reload(false) : 
                    <div className="center60">
                        {this.getEmployee()}
                        <div className="col3">
                            <Link to="/employee">
                                <img src={employee} alt="employee" />
                                <h4><i>EMPLOYEES</i></h4>
                            </Link>
                        </div>
                        <div className="col3">
                            <Link to="/statu">
                                <img src={statu} alt="statu" />
                                <h4><i>STATU</i></h4>
                            </Link>
                        </div>
                        <div className="col3">
                            <Link to="/criteria">
                                <img src={criteria} alt="criteria" />
                                <h4><i>CRITERIA</i></h4>
                            </Link>
                        </div>
                    </div>
                }

            </div>


        )
    }
}

export default index
