import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'


export class forgatPassForm extends Component {

    state = {
        username: '',
        question: '',
        answer: '',
        correctAnswer: '',
        isVisible: false,
        message: ''

    }


    findQuestion = async () => {
        if (this.state.username !== '') {
            const response = await fetch(`/api/admin/getByUsername/${this.username}`)
            const admin = await response.json();
            if (admin === []) {
                this.setState({
                    question: "there is no such user",
                    isVisible: false
                })
            } else
                this.setState({
                    question: admin.question,
                    correctAnswer: admin.answer,
                    isVisible: true
                })

        }
    }

    checkQuestion = () => {
        if (this.state.answer === this.state.correctAnswer) {
            //newpassworpage
        } else {
            this.setState({
                message: "wrong answer"
            })
        }
    }



    changeInput = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    render() {
        const { username, question, answer, message, isVisible } = this.state
        console.log(question)
        return (
            <div className="center">
                <hr />
                {
                    !isVisible ? <div>
                        <input className="formCenter" type="text" id="username" placeholder="Username" value={username} onChange={this.changeInput} />
                        <button className="formCenter" id="next" onClick={this.findQuestion}> Next </button>
                        <br />
                    </div> : <div>
                            
                            <h4 className="formCenter" id="question" >{question}</h4>
                            <br />
                            <input className="formCenter" type="text" id="answer" placeholder="Answer" value={answer} onChange={this.changeInput} />
                            <button className="formCenter" id="next2" > Next </button>
                        </div>
                }

                <br />
                <h6 className="formCenter" id="message" onChange={this.changeInput} value={message} visible={isVisible} ></h6>
                <hr />
                <Link className="formCenter" to="/login"> Login </Link>
                <br />
                <Link className="formCenter" to="/signup"> Sign Up </Link>
            </div>
        )
    }
}

export default forgatPassForm
