import React, { Component } from 'react'
import axios from 'axios'

const AdminContext = React.createContext();



const reducer = (state, action) => {
    switch (action.type) {
        case "deleteAdmin":
            return {
                /* guncellenmis state */
            }
        case "getAdminByUsername":
            return getAdminByName(action.payload.username);
               
        default:
            console.log("wrong action");
            return state
    }
}

const getAdminByName = async (name) =>{
        let a = await axios.get(`http://localhost:5000/api/admin/getByUsername/${name}`)
        console.log({a})
}

export class AdminProvider extends Component {
    
    
    state = {
        admins: [ {
            id: "-",
            name: "-",
            lastname: "-",
            username: "-",
            mail: "-",
            password: "-"
        }],
        dispatch: action => {
            this.setState(
                state => reducer(state, action)
            )
        }
        
    }
   

    render() {
        return (
            <AdminContext.Provider value={this.state}>
                {this.props.children}
            </AdminContext.Provider>
        )
    }
}

const AdminConsumer = AdminContext.Consumer;

export default AdminConsumer;
