import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import employee from '../images/employee.svg'
import statu from '../images/statu.svg'
import criteria from '../images/criteria.png'
import mainNav from '../components/mainNav'


export class index extends Component {



    state = {
        currentAdmin: JSON.parse(localStorage.getItem('currentAdmin')),


        employee:  JSON.parse(localStorage.getItem('employee')),
        statu:  JSON.parse(localStorage.getItem('statu')),
        criterion:  JSON.parse(localStorage.getItem('criterion')),
        isValid: false,



    }

    deleteLocal = () => {
        localStorage.clear();
    }


    getCurrentAdmin = async () => {
        let current = await JSON.parse(localStorage.getItem('currentAdmin'));
        this.setState({
            isValid: true
        })
        return current;
    }

    componentDidMount = async () => {
        if (this.state.currentAdmin !== null) this.setState({ isValid: true });
        
    }


    render() {
        const { isValid } = this.state;
        return (
            <div>
                {
                    (isValid) ?
                        <div className="center60">

                            <div className="col3">
                                <Link to="/employee">
                                    <img src={employee} alt="employee" />
                                    <h4><i>EMPLOYEES</i></h4>
                                </Link>
                            </div>
                            <div className="col3">
                                <Link to="/statu">
                                    <img src={statu} alt="statu" />
                                    <h4><i>STATU</i></h4>
                                </Link>
                            </div>
                            <div className="col3">
                                <Link to="/criteria">
                                    <img src={criteria} alt="criteria" />
                                    <h4><i>CRITERIA</i></h4>
                                </Link>
                            </div>
                            
                        </div> : null
                }
            </div>




        )
    }
}

export default index
