    })
}

export class AdminProvider extends Component {
    
    state = {
        admins: [ {
            id: "-",
            name: "-",
            lastname: "-",
            username: "-",
            mail: "-",
            password: "-"
        }],
        dispatch: (action, payload) => {
            reducer(action, payload);
        }
    }
