import React, { Component } from 'react'

const AdminContext = React.createContext();




export class AdminProvider extends Component {
    
    
    constructor(props) {
        super(props);

        this.state = {
            admins: [ {
                id: "-",
                name: "-",
                lastname: "-",
                username: "-",
                mail: "-",
                password: "-"
            }],
            dispatch: (action) => {
                this.reducer(action);
            }
            
        }
        

    }

    reducer = (action) => {
        switch (action.type) {
            case "deleteAdmin":
                return {
                    /* guncellenmis state */
                }
            case "getAdminByUsername":
                this.getAdminByUsername(action.payload);
                   
            default:
                console.log("wrong action");
                return this.state
        }
    }
    
    
    getAdminByUsername =  (name) =>{
            fetch(`/api/admin/getByUsername/${name}`)
            .then(res => res.json())
            .then(admin => {
              console.log(admin);
              this.setState({
                  admins: admin
              })
                console.log(this.state)
            })
        }

    render() {
        return (
            <AdminContext.Provider value={this.state}>
                {this.props.children}
            </AdminContext.Provider>
        )
    }
}

const AdminConsumer = AdminContext.Consumer;

export default AdminConsumer;
