import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import employee from '../images/employee.svg'
import statu from '../images/statu.svg'
import criteria from '../images/criteria.png'


export class index extends Component {

   

    state = {
        currentAdmin: JSON.parse(localStorage.getItem('currentAdmin')),


        employee: "",
        statu: "",
        criterion: "",

        isValid: false,

        getData: async () => {
            if (this.state.currentAdmin !== null) {
                this.setState({ isValid: true });
                await this.getEmployee();
                await this.getCriterion();
                await this.getStatu();
            }

        },


    }



    deleteLocal = () => {
        localStorage.clear();
    }

    getEmployee = async () => {
        const response = await fetch(`/api/employee/getByAdminId/${this.state.currentAdmin.loggedAdmin.id}`);
        const data = await response.json();
        await localStorage.setItem('employees', JSON.stringify(data))
    }

    getStatu = async () => {
        const response = await fetch(`/api/statu/getByAdminId/${this.state.currentAdmin.loggedAdmin.id}`);
        const data = await response.json();
        localStorage.setItem('statu', JSON.stringify(data))
    }

    getCriterion = async () => {
        const response = await fetch(`/api/criterion/getByAdminId/${this.state.currentAdmin.loggedAdmin.id}`);
        const data = await response.json();
        localStorage.setItem('criterion', JSON.stringify(data))
    }

    getCurrentAdmin = async () => {
        let current = await JSON.parse(localStorage.getItem('currentAdmin'));
        this.setState({
            isValid: true
        })
        return current;
    }

    componentDidMount = async () => {
        this.state.getData()
        this.setState({
            employee: await JSON.parse(localStorage.getItem('employee')),
            statu: await JSON.parse(localStorage.getItem('statu')),
            criterion: await JSON.parse(localStorage.getItem('criterion')),
        })
        console.log(this.state)
    }


    render() {
        const { isValid } = this.state;
        return (
            <div>
                {
                    (isValid) ?
                        <div className="center60">

                            <div className="col3">
                                <Link to="/employee">
                                    <img src={employee} alt="employee" />
                                    <h4><i>EMPLOYEES</i></h4>
                                </Link>
                            </div>
                            <div className="col3">
                                <Link to="/statu">
                                    <img src={statu} alt="statu" />
                                    <h4><i>STATU</i></h4>
                                </Link>
                            </div>
                            <div className="col3">
                                <Link to="/criteria">
                                    <img src={criteria} alt="criteria" />
                                    <h4><i>CRITERIA</i></h4>
                                </Link>
                            </div>
                            <button onClick={this.deleteLocal}>delete</button>
                            <button onClick={e => { console.log(this.state) }}>state</button>

                        </div> : null
                }
            </div>




        )
    }
}

export default index
