                            <Link to="/statu">
                                <img src={statu} alt="statu" />
                                <h4><i>STATU</i></h4>
                            </Link>
                        </div>
                        <div className="col3">
                            <Link to="/criteria">
                                <img src={criteria} alt="criteria" />
                                <h4><i>CRITERIA</i></h4>
                            </Link>
                        </div>
                        <button onClick={this.deleteLocal}> delete </button>
                    </div>
               


        )
    }
}

export default index
