     getCriteria = () => {
    
    }


    render() {
        return (
           
                     <div className="center60">
                       
                        <div className="col3">
                            <Link to="/employee">
                                <img src={employee} alt="employee" />
                                <h4><i>EMPLOYEES</i></h4>
                            </Link>
                        </div>
                        <div className="col3">
                            <Link to="/statu">
                                <img src={statu} alt="statu" />
                                <h4><i>STATU</i></h4>
                            </Link>
                        </div>
                        <div className="col3">
                            <Link to="/criteria">
                                <img src={criteria} alt="criteria" />
                                <h4><i>CRITERIA</i></h4>
                            </Link>
                        </div>
                    </div>
               


        )
    }
}

export default index
