import React, { Component } from 'react'
import { Link, useHistory } from 'react-router-dom'
import {AdminConsumer} from '../contexts/adminContext'

export class loginForm extends Component {
    state = {

        username: "gurk",
        password: "123",
        admins: [ {
            id: "-",
            name: "-",
            lastname: "-",
            username: "-",
            mail: "-",
            password: "-"
        }]
    }

    

    getAdminByUsername = (name, callback) => {
        fetch(`/api/admin/getByUsername/${name}`)
            .then(res => res.json())
            .then(admin => {
                this.setState({
                    admins: admin
                })
                callback()
            })
    }




    checkLogin = () => {
        this.getAdminByUsername(this.state.username, () => {
            

            for (let admin of this.state.admins) {
                let inputPassword = this.state.password;
                let password = admin.password;
                console.log({inputPassword,password});
                console.log(inputPassword === password)
                if (admin.getPassword === "-") {
                    console.log("not include username");
                } else if (inputPassword === password) {
                        
                    this.props.history.push("/index")
                    return;
                } else {
                    console.log("wronfg password");
                }
            }
        })


    }

    changeInput = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })

    }

    render() {
        const { username, password } = this.state
        return (
            <AdminConsumer>
                <div className="center" >
                <hr />
                <input id="username"
                    className="formCenter"
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={this.changeInput} />
                <br />
                <input id="password"
                    className="formCenter"
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={this.changeInput} />
                <hr />
                <button id="loginButton" className="formCenter" onClick={this.checkLogin}> Login </button>
                <br />
                <Link className="formCenter" to="/signup" > Sign Up </Link>
                <br />
                <Link className="formCenter" to="/forgatpass" > Forgat Password </Link>
            </div>
            </AdminConsumer>
            
        )
    }
}

export default loginForm
