import React, { Component } from 'react'

const AdminContext = React.createContext();




export class AdminProvider extends Component {

    reducer = (state, action) => {
        switch (action.type) {
            case "deleteAdmin":
                return {
                    /* guncellenmis state */
                }
            case "getAdminByUsername":
                
                this.setState({
                    admins: getAdminByUsername(action.payload)
                })
                   
            default:
                console.log("wrong action");
                return state
        }
    }
    
    getAdminByName = async (name) =>{
        await fetch(`/api/admin/getByUsername/${name}`)
        .then(res => res.json())
        .then(admin => {
          console.log(admin);
          return admin;
            
        })
    }
    
    constructor(props) {
        super(props);

        this.state = {
            admins: [ {
                id: "-",
                name: "-",
                lastname: "-",
                username: "-",
                mail: "-",
                password: "-"
            }],
            dispatch: action => {
    
                this.setState(
                    state => reducer(state, action)
                )
            }
            
        }

    }


    
   

    render() {
        return (
            <AdminContext.Provider value={this.state}>
                {this.props.children}
            </AdminContext.Provider>
        )
    }
}

const AdminConsumer = AdminContext.Consumer;

export default AdminConsumer;
