import React, { Component } from 'react'
import axios from 'axios'

const AdminContext = React.createContext();

state = {
    admins: [ {
        id: "-",
        name: "-",
        lastname: "-",
        username: "-",
        mail: "-",
        password: "-"
    }],
    dispatch: (action, payload) => {
        reducer(action, payload, state);
    }
}

const reducer = (action, payload) => {
    switch (action.type) {
        case "deleteAdmin":
            return {
                /* guncellenmis state */
            }
        case "getAdminByUsername":
            return getAdminByName(payload);
               
        default:
            console.log("wrong action");
            return this.state
    }
}

const getAdminByName = async (name) =>{
    this.setState({
        admins: await axios.get(`http://localhost:5000/api/admin/getByUsername/${name}`)
    })
}

export class AdminProvider extends Component {
    

   

    render() {
        return (
            <AdminContext.Provider value={this.state}>
                {this.props.children}
            </AdminContext.Provider>
        )
    }
}

const AdminConsumer = AdminContext.Consumer;

export default AdminConsumer;
